mapper.select:
SELECT * FROM ( SELECT SUB.*, ROWNUM AS RN FROM ( SELECT I.*, U.NAME FROM PRODUCT_INQUIRY I INNER JOIN USERS U ON I.USER_ID = U.USER_ID
AND (I.TITLE LIKE '%' || #{keyword} || '%' OR U.NAME LIKE '%' || #{keyword} || '%' OR I.USER_ID LIKE '%' || #{keyword} || '%')
AND I.STATUS = #{statusOption}
AND I.CDATE >= TO_DATE(#{startDate}, 'YYYY-MM-DD')
AND I.CDATE <= TO_DATE(#{endDate}, 'YYYY-MM-DD') + 0.99999
ORDER BY I.INQUIRY_NO DESC ) SUB ) WHERE RN > #{startIndex} AND RN <= #{startIndex} + #{pageSize}

mapper.update:
UPDATE ORDERS
SET STATUS = #{status},
    UDATE = SYSDATE
WHERE ORDER_NO = #{orderNo}
AND STATUS = #{currentStatus}

mapper.select:
SELECT PRODUCT_NO, QUANTITY
FROM ORDERS
WHERE ORDER_NO = #{orderNo}

mapper.update:
UPDATE PRODUCT
SET QUANTITY = QUANTITY + #{quantityChange}
WHERE PRODUCT_NO = #{productNo}

UPDATE PRODUCT
SET SELL_COUNT = SELL_COUNT + #{quantityChange}
WHERE PRODUCT_NO = #{productNo}

mapper.select:
SELECT PRODUCT_NO, BRAND, PRODUCT_NAME, PRICE, PRODUCT_SIZE, QUANTITY AS PRODUCT_STOCK 
FROM PRODUCT
WHERE PRODUCT_NO = #{productNo}

SELECT * FROM ( SELECT SUB.*, ROWNUM AS RN FROM ( SELECT RB.REPORT_BOARD_NO AS REPORTBOARDNO, RB.BOARD_NO AS REPORTEDBOARDNO, RB.REPORTER_ID AS REPORTERID, TO_CHAR(RB.CDATE, 'YYYY-MM-DD') AS CDATE, RB.STATUS AS REPORTSTATUS, B.TITLE AS BOARDTITLE, B.USER_ID AS BOARDUSERID, U_R.NAME AS REPORTERNAME, U_B.NAME AS BOARDAUTHORNAME FROM REPORT_BOARD RB JOIN BOARD B ON RB.BOARD_NO = B.BOARD_NO JOIN USERS U_R ON RB.REPORTER_ID = U_R.USER_ID JOIN USERS U_B ON B.USER_ID = U_B.USER_ID
AND (B.TITLE LIKE '%' || #{keyword} || '%' OR B.CONTENTS LIKE '%' || #{keyword} || '%' OR B.USER_ID LIKE '%' || #{keyword} || '%' OR RB.REPORTER_ID LIKE '%' || #{keyword} || '%')
AND RB.STATUS = #{reportStatus}
AND RB.CDATE >= TO_DATE(#{startDate}, 'YYYY-MM-DD')
AND RB.CDATE <= TO_DATE(#{endDate}, 'YYYY-MM-DD') + 0.99999
ORDER BY RB.CDATE DESC, RB.REPORT_BOARD_NO DESC ) SUB ) WHERE RN > #{startIndex} AND RN <= #{startIndex} + #{limit}

mapper.update:
UPDATE REPORT_BOARD
SET STATUS = 'Y'
WHERE REPORT_BOARD_NO = #{reportBoardNo}
