mapper.select:
SELECT COUNT(*) AS CNT FROM BOARD WHERE 1=1
AND TYPE = #{type}
AND ( TITLE LIKE '%' || #{keyword} || '%' OR USER_ID LIKE '%' || #{keyword} || '%' )

SELECT B.*, TO_CHAR(CDATE, 'YYYY-MM-DD') AS CHARDATE
FROM BOARD B
WHERE BOARD_NO = #{boardNo}

SELECT C.*, TO_CHAR(CDATE, 'YYYY-MM-DD') AS CHARDATE
FROM COMMENTS C
WHERE BOARD_NO = #{boardNo}
